package basicprograms;

public class Contains_String_Function {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Manish Kumar Tiwari";
		System.out.println(s.contains("Kumar"));//true
		String a1="Bhushan Bhimrao Kabra";
		boolean ans=a1.contains("Kumar");
		System.out.println(ans);//false
		String s1="Garima Lekhi Mahindru";
		boolean ans1=s1.contains("Mahindru");
		System.out.println(ans1);//true
		

	}

}
