package basicprograms;

public class Math_class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	System.out.println(Math.addExact(80, 20));
	System.out.println(Math.addExact(454544445555l, 454544445555l));
	System.out.println(Math.subtractExact(66, 33));
	System.out.println(Math.multiplyExact(20, 90));
	System.out.println(Math.max(8, 7));
	System.out.println(Math.min(7, 2));
	System.out.println(Math.sqrt(81));
	System.out.println(Math.random());
	System.out.println(Math.abs(-4));
	System.out.println(Math.PI);
}

}
